import React, { Component } from "react";
import ReactDOM from "react-dom";
import '@fortawesome/fontawesome-free/css/all.min.css';
import "bootstrap-css-only/css/bootstrap.min.css";
import "mdbreact/dist/css/mdb.css";
import { MDBContainer, MDBRow, MDBCol } from "mdbreact";
import "./index.css";
class App extends Component {
    constructor(props) {
      super(props);
      this.state = {
        events: [
          { time: "10:00", title: "Breakfast with Simon" },
          { time: "10:30", title: "Daily Standup Meeting (recurring)" },
          { time: "11:00", title: "Call with HRs" }
        ]
      };
    }
    myLoopFunction() {
    var myArray = [];
    var i;
    for (i = 0; i < this.state.events.length; i++) {
      myArray[i] = (
        <Event
          time={this.state.events[i].time}
          title={this.state.events[i].title}
        />
      );
    }
    return myArray;
  }
    render() {
    return (
      <React.Fragment>
        <MDBContainer>
          <MDBRow>
            <MDBCol md="9">
              <Event title="Meeting with John" time="10:00" />
            </MDBCol>
            <MDBCol md="3" />
            <MDBCol md="9"> {this.myLoopFunction()} </MDBCol>
            <MDBCol md="3" />
          </MDBRow>
        </MDBContainer>
      </React.Fragment>
    );
  }
}

class Event extends Component {
    render() {
      return (
        <React.Fragment>
          <h3>
            {this.props.time} - {this.props.title}
          </h3>
        </React.Fragment>
      );
    }
  }

ReactDOM.render(<App />, document.getElementById("root"));
