import React, { Component } from "react";
import ReactDOM from "react-dom";
import '@fortawesome/fontawesome-free/css/all.min.css';
import "bootstrap-css-only/css/bootstrap.min.css";
import "mdbreact/dist/css/mdb.css";
import { MDBContainer, MDBRow, MDBCol } from "mdbreact";
import "./index.css";
class App extends Component {
    constructor(props) {
      super(props);
      this.state = {
        events: [
  {
    id: 1,
    time: "10:00",
    title: "Breakfast with Simon",
    location: "Lounge Caffe",
    description: "Discuss Q3 targets"
  },
  {
    id: 2,
    time: "10:30",
    title: "Daily Standup Meeting (recurring)",
    location: "Warsaw Spire Office"
  },
  { id: 3, time: "11:00", title: "Call with HRs" },
  {
    id: 4,
    time: "11:00",
    title: "Lunch with Timothy",
    location: "Canteen",
    description:
      "Project evaluation ile declaring a variable and using an if statement is a fine way to conditionally render a component, sometimes you might want to use a"
         }
        ]
      };
    }
  render() {
    return (
      <React.Fragment>
        <MDBContainer>
          <MDBRow>
            <MDBCol md="9"> {this.state.events.map(event => (
    <Event
      key={event.id}
      id={event.id}
      time={event.time}
      title={event.title}
      location={event.location}
      description={event.description}
    />
    ))}
            </MDBCol>
            <MDBCol md="3" />
          </MDBRow>
        </MDBContainer>
      </React.Fragment>
    );
  }
}

class Event extends Component {
    render() {
      return (
        <React.Fragment>
          <h3>
              {this.props.time} - {this.props.title}
          </h3>
          <h6>
              {this.props.location && <h6>Location: {this.props.location}</h6>}
          </h6>
          <p>
              Desc: {this.props.description && (
    <p className="p-2 mb-4  blue-grey lighten-5 blue-grey lighten-5">
      {this.props.description}
    </p>
  )}
          </p>
        </React.Fragment>
      );
    }

  }

  function Hello(props) {
      const isLoggedIn = props.isLoggedIn;
      if (isLoggedIn) {
        return "Hello!";
      }
      return "Please login first!";
  }
    ReactDOM.render(
      // Try changing to isLoggedIn={true}:
      <Hello isLoggedIn={false} />,
      document.getElementById("root")
    );
